import React from 'react';
import './App.css';
import { marked } from "marked";

const initialState = `
**Markdown Converter**

# Written in React
## By Joseph Chikak

- Coding is fun
- I love react!
- I love anime!

[Google](https://google.com)

\`<div></div>\`

Block of code:
\`\`\`
  const a=1;
  const b=2;
  const c= a+b'
\`\`\`

![Premier League](https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.logolynx.com%2Ftopic%2Fman%2Butd&psig=AOvVaw0EaVl4Vj6zgKUcXqmw7dc_&ust=1664376046087000&source=images&cd=vfe&ved=0CAwQjRxqFwoTCPjv3IuatfoCFQAAAAAdAAAAABAI)
`
class App extends React.Component{ 

 constructor(props){
  super(props);
  this.state ={
      input: initialState
  }
 }
// to recieve changes in text and set markdown to new text
 handleChange = (event) =>{
  this.setState({
    input: event.target.value
  })
 }
  render() {
    // destructure markdown from this.state to be able to use mardown as a word 
    const {input} = this.state;

    // convert input to marked
    const markdown = marked(input);

    return (
      <div>
        <h1>Markdown Converter</h1>
        <div className='container'>
          <div className='editor'>
              <h3>Markdown here:</h3>
              <textarea id='editor' value={input} onChange={this.handleChange}/>
          </div>
          < div className='preview'>
              <h3>Results</h3>
              <div id='preview' dangerouslySetInnerHTML={{__html:markdown}}></div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
